import React, { useContext, useState, useMemo } from 'react';
import './CSS/ShopCategory.css';
import { ShopContext } from '../Context/ShopContext';
import dropdown_icon from '../Components/Assets/dropdown_icon.png';
import Item from '../Components/Item/Item';

const ShopCategory = (props) => {
  const { all_product = [] } = useContext(ShopContext);

  // Filter products based on category
  const filteredProducts = useMemo(() => {
    return all_product.filter(item => props.Category.toLowerCase() === item.category.toLowerCase());
  }, [all_product, props.Category]);

  const [page, setPage] = useState(1);
  const itemsPerPage = 12;

  const handleLoadMore = () => {
    setPage(prevPage => prevPage + 1);
  };

  const paginatedProducts = filteredProducts.slice(0, page * itemsPerPage);

  return (
    <div className="shop-category">
      <img className='shopcategory-banner' src={props.banner} alt="" />
      <div className="shopcategory-indexSort">
        <p>
          <span>Showing 1-{paginatedProducts.length}</span> out of {filteredProducts.length} products
        </p>
        <div className="shopcategory-sort">
          Sort by <img src={dropdown_icon} alt="" />
        </div>
      </div>

      <div className="shopcategory-products">
        {filteredProducts.length === 0 ? (
          <p>No products available in this category</p>
        ) : (
          paginatedProducts.map(item => (
            <Item
              key={item.id}
              id={item.id}
              name={item.name}
              image={item.image}
              new_price={item.new_price}
              old_price={item.old_price}
            />
          ))
        )}
      </div>

      <div className="shopcategory-loadmore" onClick={handleLoadMore}>
        Explore More
      </div>
    </div>
  );
};

export default ShopCategory;


























